(function(){tinymce.create("tinymce.plugins.AdvancedFormPlugin",{init:function(a,b){a.addCommand("mceAdvForm",function(){if(a.dom.getAttrib(a.selection.getNode(),"class").indexOf("mceItem")!=-1){return}a.windowManager.open({file:b+"/form.htm",width:480+parseInt(a.getLang("advform.delta_width",0)),height:385+parseInt(a.getLang("advform.delta_height",0)),inline:1},{plugin_url:b})});a.addButton("form",{title:"advform.form_desc",cmd:"mceAdvForm"})},getInfo:function(){return{longname:"Advanced form",author:"Moxiecode Systems AB",authorurl:"http://tinymce.moxiecode.com",infourl:"http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/advform",version:tinymce.majorVersion+"."+tinymce.minorVersion}}});tinymce.PluginManager.add("advform",tinymce.plugins.AdvancedFormPlugin)})();